import React, { useState } from "react";
import "./styles.css";

const InsertionSortVisualization = () => {
  const [array, setArray] = useState([12, 11, 13, 5, 6]);
  const [step, setStep] = useState(0);
  const [sortedArray, setSortedArray] = useState([...array]);

  const handleNextStep = () => {
    let newArray = [...sortedArray];
    if (step < array.length) {
      let key = newArray[step];
      let j = step - 1;
      while (j >= 0 && newArray[j] > key) {
        newArray[j + 1] = newArray[j];
        j = j - 1;
      }
      newArray[j + 1] = key;
      setSortedArray(newArray);
      setStep(step + 1);
    }
  };

  const handleReset = () => {
    setSortedArray([...array]);
    setStep(0);
  };

  return (
    <div className="container">
      <h1>Visualización de Inserción</h1>
      <div className="array-container">
        {sortedArray.map((num, index) => (
          <div
            key={index}
            className={`array-bar ${index === step ? "current" : ""}`}
          >
            {num}
          </div>
        ))}
      </div>
      <button onClick={handleNextStep} disabled={step >= array.length}>
        Siguiente Paso
      </button>
      <button onClick={handleReset}>Reiniciar</button>
    </div>
  );
};

export default InsertionSortVisualization;
